mapper.select:
SELECT * FROM
TB_DOLL_PLAY_DETAIL
WHERE
GUESS_ID = #{GUESS_ID}
AND
DOLLID = #{DOLLID}

mapper.update:
UPDATE
TB_DOLL_PLAY_DETAIL
SET
STOP_FLAG = #{STOP_FLAG}
WHERE
GUESS_ID = #{GUESS_ID}

UPDATE
TB_DOLL_PLAY_DETAIL
SET
STATE = #{STATE}
WHERE
GUESS_ID = #{GUESS_ID}
AND
DOLLID = #{DOLLID}
