mapper.select:
select c.USERID, c.DOLLID, c.DOLL_NAME_, c.STATE, c.CREATE_DATE, c.ID, d.NICKNAME, e.DOLL_NAME from TB_DOLL_PLAY_DETAIL c LEFT JOIN SYS_APP_USER d on d.USER_ID=c.USERID LEFT JOIN SYS_APP_DOLL e on e.DOLL_ID=c.DOLLID where 1=1
and( d.NICKNAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or e.DOLL_NAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
and c.STATE = #{pd.STATE}

SELECT * FROM
TB_DOLL_PLAY_DETAIL
WHERE
GUESS_ID = #{GUESS_ID}
AND
DOLLID = #{DOLLID}

mapper.update:
UPDATE
TB_DOLL_PLAY_DETAIL
SET
STOP_FLAG = #{STOP_FLAG}
WHERE
GUESS_ID = #{GUESS_ID}

UPDATE
TB_DOLL_PLAY_DETAIL
SET
STATE = #{STATE},
POST_STATE = #{POST_STATE}
WHERE
GUESS_ID = #{GUESS_ID}
AND
DOLLID = #{DOLLID}

mapper.select:
SELECT * FROM
TB_DOLL_PLAY_DETAIL
WHERE
GUESS_ID = #{GUESS_ID}
and
DOLLID = #{DOLLID}
and
USERID = #{USERID}

mapper.update:
UPDATE
TB_DOLL_PLAY_DETAIL
SET
CAMERA_DATE = #{CAMERA_DATE}
WHERE
ID = #{ID}

UPDATE
tb_doll_play_detail
SET
POST_STATE = #{POST_STATE}
WHERE
ID = #{ID}
